{
    "output": {
        "type": "plaintext"
    },
    "declaration": {
        "layer4": {
            "servers": [
                {
                    "listen": {
                        "address": "10053",
                        "protocol": "udp"
                    },
                    "upstream": "l4_upstream"
                }
            ],
            "upstreams": [
                {
                    "name": "l4_upstream",
                    "origin": [
                        {
                            "server": "10.0.0.1:53"
                        },
                        {
                            "server": "10.0.0.2:53"
                        }
                    ]
                }
            ]
        },
        "http": {
            "servers": [
                {
                    "names": [
                        "server_8080.nginx.lab",
                        "server_8081.nginx.lab"
                    ],
                    "listen": {
                        "address": "127.0.0.1:8080"
                    },
                    "log": {
                        "access": "/var/log/nginx/access_log",
                        "error": "/var/log/nginx/error_log"
                    },
                    "locations": [
                        {
                            "uri": "/test",
                            "urimatch": "exact",
                            "upstream": "http://test_upstream",
                            "health_check": {
                                "enabled": true,
                                "uri": "/healthcheck",
                                "interval": 5,
                                "fails": 3,
                                "passes": 2
                            },
                            "rate_limit": {
                                "profile": "test_ratelimit",
                                "httpcode": 429,
                                "burst": 10,
                                "delay": 3
                            },
                            "app_protect": {
                                "enabled": true,
                                "policy": "test_policy",
                                "log": {
                                    "profile_name": "okta",
                                    "enabled": true,
                                    "destination": "192.168.1.5:514"
                                }
                            },
                            "snippet": "# This is a location snippet comment"
                        }
                    ],
                    "app_protect": {
                        "enabled": true,
                        "policy": "test_policy",
                        "log": {
                            "profile_name": "default",
                            "enabled": true,
                            "destination": "192.168.1.5:514"
                        }
                    },
                    "snippet": "# This is a server snippet comment"
                },
                {
                    "names": [
                        "server_443"
                    ],
                    "listen": {
                        "address": "127.0.0.1:443",
                        "http2": true,
                        "tls": {
                            "certificate": "test_cert",
                            "key": "test_key",
                            "chain": "test_chain",
                            "ciphers": "DEFAULT",
                            "protocols": [
                                "TLSv1.2",
                                "TLSv1.3"
                            ]
                        }
                    },
                    "locations": [
                        {
                            "uri": "/",
                            "upstream": "http://test_upstream"
                        }
                    ]
                }
            ],
            "upstreams": [
                {
                    "name": "test_upstream",
                    "origin": [
                        {
                            "server": "10.0.0.1:80",
                            "weight": 5,
                            "max_fails": 2,
                            "fail_timeout": "30s",
                            "max_conns": 3,
                            "slow_start": "30s"
                        },
                        {
                            "server": "10.0.0.2:80",
                            "backup": true
                        }
                    ],
                    "sticky": {
                        "cookie": "cookie_name",
                        "expires": "1h",
                        "domain": ".testserver",
                        "path": "/"
                    },
                    "snippet": "# This is a upstream snippet comment"
                }
            ],
            "rate_limit": [
                {
                    "name": "test_ratelimit",
                    "key": "$binary_remote_addr",
                    "size": "10m",
                    "rate": "1r/s"
                }
            ],
            "nginx_plus_api": {
                "write": true,
                "listen": "127.0.0.1:8080",
                "allow_acl": "0.0.0.0/0"
            }
        }
    }
}

